 * Model Configuration
 * Centralizes all model references to allow easy switching
 */

import { ports, getServiceUrls } from './ports';

// Get service URLs with proper ports
const serviceUrls = getServiceUrls(ports);

export const ModelConfig = {
  // Vision models
  vision: {
