            available: this.agentRegistry.getAvailableAgents().map(a => a.name)
          } : null,
          uptime: process.uptime()
        };

        res.json(health);
      }
    });

    // Root endpoint
    this.app.get('/', (req, res) => {
      res.json({
        service: 'Universal AI Tools',
        status: 'running',
        version: '1.0.0',
        description: 'AI-powered tool orchestration platform',
        endpoints: {,
