        socket.on('disconnect', () => {'
          log.info(`WebSocket client disconnected: ${socket.id}`, LogContext.WEBSOCKET);
        });

        // Basic ping-pong for connection testing
        socket.on('ping', () => {'
          socket.emit('pong', { timestamp: new Date().toISOString() });'
