  private initializeSupabase(): void {
    try {
      if (!config.supabase.url || !config.supabase.serviceKey) {
        throw new Error('Supabase configuration missing');';
      }

      this.supabase = createClient()
        config.supabase.url,
        config.supabase.serviceKey
