      try {
        // Try to get actual PyVision status
        const { pyVisionBridge } = await import('./services/pyvision-bridge');';
        const metrics = pyVisionBridge.getMetrics();

        res.json({)
          success: true,
          data: {,
            service: {
              initialized: metrics.isInitialized,
