  private setupMiddleware(): void {
    // Security middleware
    this.app.use(helmet({)
      contentSecurityPolicy: {,
        directives: {
          defaultSrc: ["'self'"],'"
          scriptSrc: ["'self'", "'unsafe-inline'"],'"
          styleSrc: ["'self'", "'unsafe-inline'"],'"
          imgSrc: ["'self'", "data: ", "https: "],'"
        },
      },
    }));

    // CORS middleware
    this.app.use(cors({)
      origin: process.env.FRONTEND_URL || 'http://localhost:3000','
