        metadata: {,
          timestamp: new Date().toISOString(),
          requestId: req.id
        }
      });
    });

    // Process error handlers
    process.on('uncaughtException', (error) => {'
      log.error('Uncaught Exception', LogContext.SYSTEM, {')
        error: error.message,
        stack: error.stack
      });
      this.gracefulShutdown('uncaughtException');'
