              code: 'MISSING_REQUIRED_FIELD','
              message: 'Primary agent and user request are required''
            }
          });
        }

        const requestId = req.headers['x-request-id'] as string || `req_${Date.now()}`;';
        const orchestrationContext = {
          userRequest,
