          }
        });
        return;

      } catch (error) {
        const errorMessage = error instanceof Error ? error.message: String(error);
        log.error('Agent orchestration error', LogContext.API, {')
          error: errorMessage
        });

        res.status(500).json({)
          success: false,
          error: {,
            code: 'ORCHESTRATION_ERROR','
