      temperature: request.parameters?.temperature || 0.5,
      max_tokens: request.parameters?.max_length || 200
    };

    try {
      const response = await fetch(`${this.baseUrl}/v1/chat/completions`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
