  private isInitialized: boolean = false;
  private requestQueue: Array<{,
    id: string;
    request: LFM2Request;,
    resolve: (response: LFM2Response) => void;,
    reject: (error: Error) => void;
  }> = [];
  private pendingRequests: Map<string, {
    resolve: (response: LFM2Response) => void;,
