    const batchSize = 3;
    const results: LFM2Response[] = [];

    for (let i = 0; i < requests.length; i += batchSize) {
      const batch = requests.slice(i, i + batchSize);
      const batchResults = await Promise.all();
        batch.map(request => this.generate(request))
