    };
  }

  private generateMockResponse(request: LFM2Request): LFM2Response {
    // Fast mock response for development/testing
    const mockContent = `Mock LFM2 response for: ${request.prompt.substring(0, 50)}...`;

    return {
      content: mockContent,
