    await new Promise(resolve => setTimeout(resolve, 2000));

    this.initializeLFM2();
  }

  public getMetrics(): LFM2Metrics & { isInitialized: boolean } {
    return {
      ...this.metrics,
      isInitialized: this.isInitialized
    };
