          estimatedTokens: parsed.tokens || 100
        };
      }
    } catch (error) {
      log.warn(' Failed to parse LFM2 routing response', LogContext.AI);'
    }

    // Fallback parsing
    return {
      targetService: 'ollama' as const,'
      confidence: 0.5,
      reasoning: 'Fallback routing due to parsing error','
      estimatedTokens: 100
    };
  }

  private parseCoordinationResponse(content: string): any {
    try {
      const jsonMatch = content.match(/\{.*\}/s);
      if (jsonMatch) {
        return JSON.parse(jsonMatch[0]);
      }
    } catch (error) {
