  }

  public async shutdown(): Promise<void> {
    log.info(' Shutting down LFM2 bridge service', LogContext.AI);'

    if (this.pythonProcess) {
      this.pythonProcess.kill();
      this.pythonProcess = null;
    }

    this.isInitialized = false;
