  private createCoordinationPrompt(primaryTask: string, supportingTasks: string[]): string {
    return `AGENT COORDINATION PLAN: ;

PRIMARY, TASK: "${primaryTask}""
SUPPORTING TASKS: ${supportingTasks.map((task, i) => `${i+1}. "${task}"`).join(', ')}'"

