  }

  private parseRoutingResponse(content: string): any {
    try {
      const jsonMatch = content.match(/{.*\}/s);
      if (jsonMatch) {
        const parsed = JSON.parse(jsonMatch[0]);
        return {
          targetService: parsed.service || 'ollama','
          confidence: parsed.confidence || 0.7,
          reasoning: parsed.reasoning || 'Automatic routing decision','
          estimatedTokens: parsed.tokens || 100
        };
      }
    } catch (error) {
      log.warn(' Failed to parse LFM2 routing response', LogContext.AI);'
    }

    // Fallback parsing
    return {
      targetService: 'ollama' as const,'
      confidence: 0.5,
      reasoning: 'Fallback routing due to parsing error','
      estimatedTokens: 100
    };
  }

  private parseCoordinationResponse(content: string): any {
    try {
      const jsonMatch = content.match(/\{.*\}/s);
      if (jsonMatch) {
        return JSON.parse(jsonMatch[0]);
      }
    } catch (error) {
      log.warn(' Failed to parse LFM2 coordination response', LogContext.AI);'
    }

    // Fallback coordination plan
    return {
      execution_plan: {,
        primary_priority: 1,
        supporting_priorities: [2, 3],
        parallel_execution: true,
        estimated_total_time: 30
      },
      resource_allocation: {,
        primary_service: 'ollama','
        supporting_services: ['lfm2', 'ollama']'
