   * Get job by ID
   */
  public async getJob(jobId: string): Promise<FineTuningJob | null> {
    try {
      const { data, error } = await this.supabase
        .from('mlx_fine_tuning_jobs')
        .select('*')
        .eq('id', jobId)
        .single();

      if (error || !data) return null;

      return this.mapDatabaseJobToJob(data);
