        completed_trials: experiment.trials.filter(t => t.status === 'completed').length,'
        best_trial_id: experiment.bestTrial?.id,
        best_metrics: experiment.bestTrial?.metrics || {},
        trials: experiment.trials,
        completed_at: experiment.completedAt,
        updated_at: new Date().toISOString()
      })
      .eq('id', experiment.id);'

    if (error) throw error;
  }

  private async updateJobQueueInDatabase(): Promise<void> {
    // In a real implementation, you would update the queue in the database
    // For now, we'll just log the queue status'
    log.debug(' Job queue updated', LogContext.AI, { '
      queueLength: this.jobQueue.length,
      running: this.activeJobs.size
    });
  }

  private mapDatabaseJobToJob(dbJob: any): FineTuningJob {
    return {
      id: dbJob.id,
      jobName: dbJob.job_name,
      userId: dbJob.user_id,
      status: dbJob.status,
      baseModelName: dbJob.base_model_name,
      baseModelPath: dbJob.base_model_path,
      outputModelName: dbJob.output_model_name,
      outputModelPath: dbJob.output_model_path,
      datasetPath: dbJob.dataset_path,
      datasetFormat: dbJob.dataset_format,
      hyperparameters: dbJob.hyperparameters,
      validationConfig: dbJob.validation_config,
      progress: {,
        currentEpoch: dbJob.current_epoch,
        totalEpochs: dbJob.total_epochs,
        currentStep: dbJob.current_step,
        totalSteps: dbJob.total_steps,
        progressPercentage: dbJob.progress_percentage,
        lastUpdateTime: new Date(dbJob.updated_at)
      },
      metrics: dbJob.training_metrics,
      evaluation: null, // Would be loaded separately
      resourceUsage: {,
        memoryUsageMB: dbJob.memory_usage_mb,
        gpuUtilizationPercentage: dbJob.gpu_utilization_percentage,
        estimatedDurationMinutes: dbJob.estimated_duration_minutes,
        actualDurationMinutes: dbJob.actual_duration_minutes
      },
      error: dbJob.error_message ? {,
        message: dbJob.error_message,
        details: dbJob.error_details,
        retryCount: dbJob.retry_count,
        maxRetries: 3,
        recoverable: true
      } : undefined,
      createdAt: new Date(dbJob.created_at),
      startedAt: dbJob.started_at ? new Date(dbJob.started_at) : undefined,
      completedAt: dbJob.completed_at ? new Date(dbJob.completed_at) : undefined,
      updatedAt: new Date(dbJob.updated_at)
    };
  }
}

