  userId: string;,
  status: JobStatus;
  baseModelName: string;,
  baseModelPath: string;
  outputModelName: string;,
  outputModelPath: string;
  datasetPath: string;,
  datasetFormat: 'json' | 'jsonl' | 'csv';'
  hyperparameters: Hyperparameters;,
  validationConfig: ValidationConfig;
