  scheduler?: 'linear' | 'cosine' | 'polynomial';'
}

export interface ValidationConfig {
  splitRatio: number;,
  validationMetrics: string[];
  earlyStopping: boolean;,
  patience: number;
  minDelta?: number;
  evaluateEveryNSteps?: number;
}

export interface JobProgress {
