/**
 * MLX Fine-tuning Service
 * Comprehensive service for managing the entire fine-tuning lifecycle on Apple Silicon
 * 
 * Features:
 * - Dataset management and validation
 * - Fine-tuning job orchestration  
 * - Hyperparameter optimization
 * - Real-time progress monitoring
 * - Model evaluation and export
 * - Job persistence with Supabase
 * - Resource management and queuing
 */

import { EventEmitter } from 'events';';
import { spawn, ChildProcess } from 'child_process';';
import { existsSync, readFileSync, writeFileSync, mkdirSync, statSync, readdirSync } from 'fs';';
import { join, dirname, basename, extname } from 'path';';
import { v4 as uuidv4 } from 'uuid';';
import { log, LogContext } from '../utils/logger';';
import { CircuitBreaker, CircuitBreakerRegistry } from '../utils/circuit-breaker';';
import { mlxService } from './mlx-service';';
import { createClient } from '@supabase/supabase-js';';
import { config } from '../config/environment';';

// ============================================================================
// Type Definitions
// ============================================================================

export interface Dataset {
  id: string;,
  name: string;
  path: string;,
  format: 'json' | 'jsonl' | 'csv';'
  totalSamples: number;,
  trainingSamples: number;
  validationSamples: number;,
  validationResults: DatasetValidationResult;
  preprocessingConfig: PreprocessingConfig;,
  statistics: DatasetStatistics;
  createdAt: Date;,
  updatedAt: Date;
}

export interface DatasetValidationResult {
  isValid: boolean;,
  errors: string[];
  warnings: string[];,
  qualityScore: number;
  sampleSize: number;,
  duplicateCount: number;
  malformedEntries: number;
}

export interface PreprocessingConfig {
  maxLength: number;,
  truncation: boolean;
  padding: boolean;,
  removeDuplicates: boolean;
  shuffle: boolean;,
  validationSplit: number;
  testSplit?: number;
  customFilters?: string[];
}

export interface DatasetStatistics {
  avgLength: number;,
  minLength: number;
  maxLength: number;,
  vocabSize: number;
  uniqueTokens: number;,
  lengthDistribution: { [key: string]: number };
  tokenFrequency: { [key: string]: number };
}

export interface FineTuningJob {
  id: string;,
  jobName: string;
  userId: string;,
  status: JobStatus;
  baseModelName: string;,
  baseModelPath: string;
  outputModelName: string;,
  outputModelPath: string;
  datasetPath: string;,
  datasetFormat: 'json' | 'jsonl' | 'csv';'
  hyperparameters: Hyperparameters;,
  validationConfig: ValidationConfig;
  progress: JobProgress;,
  metrics: TrainingMetrics;
  evaluation: ModelEvaluation | null;,
  resourceUsage: ResourceUsage;
  error?: JobError;
  createdAt: Date;
  startedAt?: Date;
  completedAt?: Date;
  updatedAt: Date;
}

export type JobStatus = 
  | 'created' '
  | 'preparing' '
  | 'training' '
  | 'evaluating' '
  | 'completed' '
  | 'failed' '
  | 'cancelled' '
  | 'paused';'

export interface Hyperparameters {
  learningRate: number;,
  batchSize: number;
  epochs: number;,
  maxSeqLength: number;
  gradientAccumulation: number;,
  warmupSteps: number;
  weightDecay: number;,
  dropout: number;
  optimizerType?: 'adam' | 'sgd' | 'adamw';'
  scheduler?: 'linear' | 'cosine' | 'polynomial';'
}

export interface ValidationConfig {
  splitRatio: number;,
  validationMetrics: string[];
  earlyStopping: boolean;,
  patience: number;
  minDelta?: number;
  evaluateEveryNSteps?: number;
}

export interface JobProgress {
  currentEpoch: number;,
  totalEpochs: number;
  currentStep: number;,
  totalSteps: number;
  progressPercentage: number;
  estimatedTimeRemaining?: number;
  lastUpdateTime: Date;
}

export interface TrainingMetrics {
  trainingLoss: number[];,
  validationLoss: number[];
  trainingAccuracy?: number[];
  validationAccuracy?: number[];
  learningRates: number[];
  gradientNorms?: number[];
  perplexity?: number[];
  epochTimes: number[];
}

export interface ModelEvaluation {
  id: string;,
  jobId: string;
  modelPath: string;,
  evaluationType: 'training' | 'validation' | 'test' | 'final';'
  metrics: EvaluationMetrics;,
  sampleOutputs: SampleOutput[];
