  message: string;,
  details: any;
  retryCount: number;,
  maxRetries: number;
  recoverable: boolean;
}

export interface HyperparameterOptimization {
  id: string;,
  experimentName: string;
  baseJobId: string;,
  userId: string;
  optimizationMethod: 'grid_search' | 'random_search' | 'bayesian' | 'genetic';,'
  parameterSpace: ParameterSpace;
  status: 'created' | 'running' | 'completed' | 'failed' | 'cancelled';,'
  trials: OptimizationTrial[];
  bestTrial?: OptimizationTrial;
  createdAt: Date;
  completedAt?: Date;
}

export interface ParameterSpace {
  learningRate: {, min: number; max: number; step?: number } | number[];
  batchSize: number[];,
  epochs: { min: number;, max: number } | number[];
  dropout: {, min: number; max: number; step?: number };
  weightDecay: {, min: number; max: number; step?: number };
  [key: string]: any;
}

export interface OptimizationTrial {
  id: string;,
  parameters: Hyperparameters;
  metrics: EvaluationMetrics;,
  status: 'running' | 'completed' | 'failed';'
  startTime: Date;
  endTime?: Date;
  jobId?: string;
}

export interface JobQueue {
  id: string;,
  jobId: string;
  priority: number;,
  queuePosition: number;
  estimatedResources: {,
    memoryMB: number;
    gpuMemoryMB: number;,
    durationMinutes: number;
  };
  dependsOnJobIds: string[];,
  status: 'queued' | 'running' | 'completed' | 'failed' | 'cancelled';'
  scheduledAt?: Date;
