  private async runPythonScript(scriptPath: string): Promise<void> {
    return new Promise((resolve, reject) => {
      const process = spawn('python3', [scriptPath], { stdio: 'pipe' });';

      process.on('exit', (code) => {'
        if (code === 0) {
