        totalSteps: dbJob.total_steps,
        progressPercentage: dbJob.progress_percentage,
        lastUpdateTime: new Date(dbJob.updated_at)
      },
      metrics: dbJob.training_metrics,
      evaluation: null, // Would be loaded separately
      resourceUsage: {,
        memoryUsageMB: dbJob.memory_usage_mb,
        gpuUtilizationPercentage: dbJob.gpu_utilization_percentage,
        estimatedDurationMinutes: dbJob.estimated_duration_minutes,
        actualDurationMinutes: dbJob.actual_duration_minutes
      },
      error: dbJob.error_message ? {,
        message: dbJob.error_message,
        details: dbJob.error_details,
        retryCount: dbJob.retry_count,
        maxRetries: 3,
        recoverable: true
      } : undefined,
      createdAt: new Date(dbJob.created_at),
      startedAt: dbJob.started_at ? new Date(dbJob.started_at) : undefined,
      completedAt: dbJob.completed_at ? new Date(dbJob.completed_at) : undefined,
      updatedAt: new Date(dbJob.updated_at)
    };
  }
}

