  private detectDatasetFormat(filePath: string): 'json' | 'jsonl' | 'csv' {'
    const ext = extname(filePath).toLowerCase();
    switch (ext) {
      case '.json': return 'json';'
      case '.jsonl': return 'jsonl';'
      case '.csv': return 'csv';'
      default: return 'jsonl';'
    }
  }

  private async readDatasetFile(filePath: string, format: string): Promise<any[]> {
    const content = readFileSync(filePath, 'utf8');';

    switch (format) {
      case 'json':'
        return JSON.parse(content);
      case 'jsonl':'
        return content.split('n').filter(line => line.trim()).map(line => JSON.parse(line));';
      case 'csv':'
        // Simple CSV parsing - in production, use a proper CSV library
        const lines = content.split('\n').filter(line => line.trim());';
        const headers = lines[0].split(',');';
        return lines.slice(1).map(line => {);
          const values = line.split(',');';
          const obj: any = {};
          headers.forEach((header, i) => {
            obj[header.trim()] = values[i]?.trim();
          });
          return obj;
        });
      default: throw new Error(`Unsupported, format: ${format}`);
    }
  }

  private async preprocessDataset(data: any[], config: PreprocessingConfig): Promise<any[]> {
    let processed = [...data];

    // Remove duplicates
    if (config.removeDuplicates) {
      const seen = new Set();
      processed = processed.filter(item => {)
        const key = `${item.input}|${item.output}`;
        if (seen.has(key)) return false;
        seen.add(key);
        return true;
      });
    }

    // Shuffle
    if (config.shuffle) {
      for (let i = processed.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [processed[i], processed[j]] = [processed[j], processed[i]];
      }
    }

    // Truncate if needed
    if (config.maxLength > 0) {
      processed = processed.map(item => ({)
        ...item,
        input: config.truncation && item.input.length > config.maxLength 
          ? item.input.substring(0, config.maxLength) 
          : item.input,
        output: config.truncation && item.output.length > config.maxLength 
          ? item.output.substring(0, config.maxLength) 
          : item.output
      }));
    }

    return processed;
  }

  private async calculateDatasetStatistics(data: any[]): Promise<DatasetStatistics> {
    const lengths = data.map(item => (item.input + ' ' + item.output).length);';
    const allText = data.map(item => item.input + ' ' + item.output).join(' ');';
    const tokens = allText.split(/s+/);
    const uniqueTokens = new Set(tokens);

    // Simple token frequency calculation
    const tokenFreq: { [key: string]: number } = {};
    tokens.forEach(token => {)
      tokenFreq[token] = (tokenFreq[token] || 0) + 1;
    });

    // Length distribution
    const lengthDistribution: { [key: string]: number } = {};
    lengths.forEach(length => {)
      const bucket = Math.floor(length / 100) * 100;
      lengthDistribution[bucket.toString()] = (lengthDistribution[bucket.toString()] || 0) + 1;
    });

    return {
      avgLength: lengths.reduce((a, b) => a + b, 0) / lengths.length,
      minLength: Math.min(...lengths),
      maxLength: Math.max(...lengths),
      vocabSize: uniqueTokens.size,
      uniqueTokens: uniqueTokens.size,
      lengthDistribution,
      tokenFrequency: tokenFreq
    };
  }

  private async saveProcessedDataset(data: any[], filePath: string): Promise<void> {
    const content = data.map(item => JSON.stringify(item)).join('\n');';
    writeFileSync(filePath, content, 'utf8');'
  }

